import { PureComponent } from 'react'

interface props {
    navigation: any
}

interface state {
    numLot: number;
    error: string;
}

export default class EnterLotController extends PureComponent<props, state> {
    constructor(props: any) {
        super(props);
        this.state = {
            numLot: 0,
            error: '',
        }
    }

    validateInput = (num: number) => {
        if (num === 0) {
            this.setState({ error: "Please enter lot" })
            return false;
        } else {
            if (num < 10) {
                this.setState({ error: "Lot number should be greater than 10" })
                return false;
            }
            return true;
        }
    }

    onChangeTextNumLot = (num: string) => {
        var val = Number(num);
        this.setState({ numLot: val });
    }

    onPressSubmit = () => {
        let lot = this.state.numLot
        let success = this.validateInput(lot);
        if (success) {
            this.setState({ error: "" })
            this.props.navigation.navigate('Home', { item: this.state.numLot })
        }
    }
}
