import { PureComponent } from 'react'

interface props {
    navigation: any
}

interface state {
    strLot: string;
    error: string;
}

export default class EnterLotController extends PureComponent<props, state> {
    constructor(props: any) {
        super(props);
        this.state = {
            strLot: '',
            error: '',
        }
    }

    validateInput = (strLot: string) => {
        let isnum = /^\d+$/.test(strLot);

        if(isnum) {
            let num = Number(strLot);
            if (num === 0) {
                this.setState({ error: "Please enter lot" })
                return false;
            } else {
                if (num < 10) {
                    this.setState({ error: "Lot number should be greater than 10" })
                    return false;
                }
                return true;
            }
        } else {
            this.setState({ error: "Lot number accepts numbers only." })
            return false;
        }
    }

    onChangeTextNumLot = (val: string) => {
        this.setState({ strLot: val });

    }

    onPressSubmit = () => {
        let lot = this.state.strLot
        let success = this.validateInput(lot);
        if (success) {
            this.setState({ error: "", numLot: 0 })
            this.props.navigation.navigate('Home', { item: lot })
        }
    }
}
