import { Component } from 'react'
import { lot } from '../model/lot';

interface props {
    navigation: any;
}

interface state {
    numLots: number;
    arrLot: Array<lot>;
}

export default class HomeController extends Component<props, state> {
    constructor(props: any) {
        super(props);
        console.log("props lot", props.route.params.item);

        this.state = {
            numLots: props.route.params.item,
            arrLot: [],
        }
    }

    componentDidMount() {
        this.setupAnArray();
    }

    setupAnArray = () => {
        const { numLots, arrLot } = this.state;
        let tmp: number = numLots;
        var temp: Array<lot> = [];
        for (let i = 0; i < numLots; i++) {
            let dic: lot = {
                id: "A" + `${i}`,
                carNum: "",
                isAlloted: false,
                inTime: "",
                outTime: "",
            }
            arrLot.push(dic);
        }

        this.setState({});
        console.log("this", this.state.arrLot);
    }

    onPressAddCar = () => {
        let min: number = 0;
        let max: number = (this.state.numLots - 1);

        let random = min + (Math.random() * (max - min))

        let tmp = this.state.arrLot[random];

        if(tmp.isAlloted) {
            this.onPressAddCar()
        }
    }

    onPressExitCar = (index: number) => {

    }
}
